mapper.select:
SELECT *
FROM facturacion.gastos
WHERE DAY(fecha) = #{ncuenta} AND MONTH(fecha) = #{monto} AND YEAR(fecha) = #{concepto}

SELECT *
FROM facturacion.gastos
WHERE fecha BETWEEN #{monto} AND #{concepto}

SELECT *
FROM facturacion.gastos
WHERE MONTH(fecha) = #{monto} AND YEAR(fecha) = #{concepto}

mapper.update:
UPDATE facturacion.gastos
SET idgastos = #{idgastos}, monto = #{monto},
    concepto = #{concepto}, ncuenta = #{ncuenta}, tipoPago = #{tipoPago},
    fecha = #{fecha}, usuario_cedula = #{usuario_cedula}
WHERE idgastos = #{idgastos}

UPDATE facturacion.gastos
SET estado = #{estado}
WHERE idgastos = #{idgastos}
